mapper.update:
UPDATE lagarto.supplies
set
isdel = #{isdel}
where iboard = ${iboard}

update cart
set num = num+1
where iboard = #{iboard} and iuser = #{iuser}

update cart
set num = num-1
where iboard = #{iboard} and iuser = #{iuser}

update lagarto_user
set money = #{balance} payment = 1
where iuser=#{iuser}
